INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('c6c00125-139c-45ea-8471-ae4a0fcc86da',
        'Как отмечает Брайзендайн, даже если принимать во внимание размеры тела, мужской мозг на 9 % больше женского. Однажды было озвучено мнение, что этот факт доказывает, что женщины не настолько умны, как мужчины. На самом деле мужчины и женщины обладают равным количеством мозговых клеток, просто женские клетки расположены в черепе более компактно.  Как отмечает Брайзендайн, даже если принимать во внимание размеры тела, мужской мозг на 9 % больше женского.  В тех участках мозга, которые отвечают за речь и слух, у женщин присутствует на 11 % нейронов больше, чем у мужчин. Часть мозга, отвечающая за консолидацию памяти - гиппокамп, также больше у женщин. Система, отвечающая за наблюдение за эмоциями и мимикой других людей, опять же более развита у женщин. Таким образом, в том, что касается речи, эмоционального интеллекта и умения хранить яркие воспоминания, женщины имеют природное преимущество перед мужчинами.  У мужчин, напротив, основная часть процессов происходит в мозжечковых миндалинах - части мозга, отвечающей за регулирование страха и агрессии. Возможно, это объясняет, почему мужчины больше склонны к проявлению гнева и насильственным действиям в ответ на возникшую перед ними физическую угрозу. Женский мозг также способен реагировать на возможные ситуации, угрожающие жизни, однако он делает это по-своему. В одних и тех же обстоятельствах женский мозг переживает больше стресса, чем мужской, и этот стресс является своеобразной ответной реакцией на все возможные опасности, которым может подвергнуться ее ребенок или семья. Именно по этой причине, утверждает Брайзендайн, современная женщина может расценивать неоплаченные счета как катастрофу, поскольку они кажутся ей прямой угрозой, ставящей под вопрос выживание ее семьи.',
        'Как отмечает Брайзендайн, даже если принимать во внимание размеры тела, мужской мозг на 9 % больше женского. Однажды было озвучено мнение, что этот факт доказывает, что женщины не настолько умны, как мужчины. На самом деле мужчины и женщины обладают равным количеством мозговых клеток, просто женские клетки расположены в черепе более компактно.  Как отмечает Брайзендайн, даже если принимать во внимание размеры тела, мужской мозг на 9 % больше женского.  В тех участках мозга, которые отвечают за речь и слух, у женщин присутствует на 11 % нейронов больше, чем у мужчин. Часть мозга, отвечающая за консолидацию памяти - гиппокамп, также больше у женщин. Система, отвечающая за наблюдение за эмоциями и мимикой других людей, опять же более развита у женщин. Таким образом, в том, что касается речи, эмоционального интеллекта и умения хранить яркие воспоминания, женщины имеют природное преимущество перед мужчинами.  У мужчин, напротив, основная часть процессов происходит в мозжечковых миндалинах - части мозга, отвечающей за регулирование страха и агрессии. Возможно, это объясняет, почему мужчины больше склонны к проявлению гнева и насильственным действиям в ответ на возникшую перед ними физическую угрозу. Женский мозг также способен реагировать на возможные ситуации, угрожающие жизни, однако он делает это по-своему. В одних и тех же обстоятельствах женский мозг переживает больше стресса, чем мужской, и этот стресс является своеобразной ответной реакцией на все возможные опасности, которым может подвергнуться ее ребенок или семья. Именно по этой причине, утверждает Брайзендайн, современная женщина может расценивать неоплаченные счета как катастрофу, поскольку они кажутся ей прямой угрозой, ставящей под вопрос выживание ее семьи.',
        'Том Батлер-Боудон', '50 великих книг по психологии', null, 529906516, '2023-11-11 07:46:30.351344',
        '2023-11-11 07:46:30.351358', 'UNREAD', 50);
