INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('93f2c474-da9e-4227-b7e8-058ac0a6ef0b',
        'Психоаналитик обнаруживает, что одна и та же человеческая реальность может скрываться за различными религиями, а также что противоположные человеческие установки могут лежать в основании одной и той же религии. Например, человеческая реальность за учениями Будды, Исаии, Христа, Сократа или Спинозы, в сущности, одна и та же. Она определяется стремлением к любви, истине и справедливости. Человеческие реальности за теологической системой Кальвина и за авторитарными политическими системами также очень похожи. По духу это — повиновение власти и отсутствие любви и уважения к индивиду. Подобно тому как родительская забота о ребенке может быть выражением любви, но может выражать и желание осуществлять контроль и господство, также и религиозное утверждение может выражать совершенно противоположные человеческие установки.',
        'Психоаналитик обнаруживает, что одна и та же человеческая реальность может скрываться за различными религиями, а также что противоположные человеческие установки могут лежать в основании одной и той же религии. Например, человеческая реальность за учениями Будды, Исаии, Христа, Сократа или Спинозы, в сущности, одна и та же. Она определяется стремлением к любви, истине и справедливости. Человеческие реальности за теологической системой Кальвина и за авторитарными политическими системами также очень похожи. По духу это — повиновение власти и отсутствие любви и уважения к индивиду. Подобно тому как родительская забота о ребенке может быть выражением любви, но может выражать и желание осуществлять контроль и господство, также и религиозное утверждение может выражать совершенно противоположные человеческие установки.',
        'Эрих Фромм', 'Психоанализ и религия', null, -1373793631, '2023-11-11 07:46:30.376942',
        '2023-11-11 07:46:30.376964', 'UNREAD', 50);
