INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('c789a3f4-a64d-4262-b400-470572c7cfb0',
        'В гуманистической религии бог — образ высшей человеческой самости, символ того, чем человек потенциально является или каким он должен стать; в авторитарной религии бог — единственный обладатель того, что первоначально принадлежало человеку: он владеет его разумом и его любовью. Чем совершеннее бог, тем несовершеннее человек. Человек проецирует лучшее, что у него имеется на бога и тем самым обедняет себя. Теперь вся любовь, мудрость и справедливость принадлежат богу, человек же лишен этих качеств, он опустошен и обездолен. Начав с чувства собственной малости, он стал теперь совершенно безвластным и лишился силы; все его силы спроецированы на бога. Такую же проекцию можно иногда наблюдать в межличностных отношениях мазохистского типа, когда один человек внушает благоговение другому, и тот приписывает ему свои собственные силы и стремления.',
        'В гуманистической религии бог — образ высшей человеческой самости, символ того, чем человек потенциально является или каким он должен стать; в авторитарной религии бог — единственный обладатель того, что первоначально принадлежало человеку: он владеет его разумом и его любовью. Чем совершеннее бог, тем несовершеннее человек. Человек проецирует лучшее, что у него имеется на бога и тем самым обедняет себя. Теперь вся любовь, мудрость и справедливость принадлежат богу, человек же лишен этих качеств, он опустошен и обездолен. Начав с чувства собственной малости, он стал теперь совершенно безвластным и лишился силы; все его силы спроецированы на бога. Такую же проекцию можно иногда наблюдать в межличностных отношениях мазохистского типа, когда один человек внушает благоговение другому, и тот приписывает ему свои собственные силы и стремления.',
        'Эрих Фромм', 'Психоанализ и религия', null, -273560554, '2023-11-11 07:46:30.380786',
        '2023-11-11 07:46:30.380803', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
