INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('ce6f8e24-f2e0-4e6b-b6ff-8f865a82f48b',
        'Размышляя над тем, что сказал ему жулик, Юнг думает, что есть некоторая красота в том, что достигший дна может идти только вверх. Он, похоже, симпатизирует «приземлённому» человеку и пишет: «Мы [все] стоим на колючих камнях несчастья и смерти». Как дочь учёного представляла тот аспект в Юнге, который требовал допуска в психику, заброшенный, а теперь мёртвый бродяга «просит, чтобы его пустили» в душу Юнга, и Юнг должен найти способ дать ему вход. Юнг осознаёт, что сам должен пережить «нижайшее» в себе, чтобы была возможность личного обновления. Он предполагает, что, в отличие от переживания «высот», которые уникальны для каждого человека, достижение дна помещает человека в контакт с ближним так, как никогда не сделают этого успех и достижение. Далее, нужно осознавать свои глубины: если человек никогда не достигал дна, опасно воображать, что это невозможно. Способность упасть есть в каждом из нас, и мы ограничиваем свой «человеческий потенциал», когда приписываем другим «приземлённость», которую якобы никогда не пережить нам.',
        'Размышляя над тем, что сказал ему жулик, Юнг думает, что есть некоторая красота в том, что достигший дна может идти только вверх (RB, р. 265b). Он, похоже, симпатизирует «приземлённому» человеку и пишет: «Мы [все] стоим на колючих камнях несчастья и смерти» (RB, р. 266а). Как дочь учёного представляла тот аспект в Юнге, который требовал допуска в психику, заброшенный, а теперь мёртвый бродяга «просит, чтобы его пустили» (RB, р. 266а) в душу Юнга, и Юнг должен найти способ дать ему вход. Юнг осознаёт, что сам должен пережить «нижайшее» в себе, чтобы была возможность личного обновления. Он предполагает, что, в отличие от переживания «высот», которые уникальны для каждого человека, достижение дна помещает человека в контакт с ближним так, как никогда не сделают этого успех и достижение. Далее, нужно осознавать свои глубины: если человек никогда не достигал дна, опасно воображать, что это невозможно. Способность упасть есть в каждом из нас, и мы ограничиваем свой «человеческий потенциал», когда приписываем другим «приземлённость», которую якобы никогда не пережить нам.',
        'Дроб Сэнфорд', 'Читая Красную книгу. Руководство по Liber Novus К. Г. Юнга', null, 1497934112,
        '2023-11-11 07:46:57.417391', '2023-11-11 07:46:57.417392', 'UNREAD', 50);
