INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('0f611c86-f917-4df3-bf7e-4686a6dc7086',
        'Шолъц: "В сновидении - истина; несмотря на маску величия или унижения, мы всегда узнаем самих себя. Честный человек не совершит и в сновидении бесчестного поступка, если же совершит, то сам возмутится им, как чем то несвойственным его натуре. Римский император, приказавший казнить одного из своих подданных за то, что тому снилось, будто он отрубил ему голову, был не так уже неправ, когда оправдывался тем, что тот, кто видит подобные сны, преисполнен таких же мыслей и в бодрственном состоянии. О том, что не укладывается в нашем сознании, мы говорим поэтому очень метко: "Мне и во сне это не снилось".',
        'Шолъц: "В сновидении - истина; несмотря на маску величия или унижения, мы всегда узнаем самих себя. Честный человек не совершит и в сновидении бесчестного поступка, если же совершит, то сам возмутится им, как чем то несвойственным его натуре. Римский император, приказавший казнить одного из своих подданных за то, что тому снилось, будто он отрубил ему голову, был не так уже неправ, когда оправдывался тем, что тот, кто видит подобные сны, преисполнен таких же мыслей и в бодрственном состоянии. О том, что не укладывается в нашем сознании, мы говорим поэтому очень метко: "Мне и во сне это не снилось".',
        'Зигмунд Фрейд', 'Толкование сновидений', null, 566324454, '2023-11-11 07:46:36.961322',
        '2023-11-11 07:46:36.961328', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
