INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('ebe7473d-9f97-42e7-925e-04ff80a19f35',
        'В технике управляемого воображения консультанты используют три типа процессов.  Спонтанное воображение возникает без сознательной направленности содержания. Направленное воображение означает, что консультант предлагает определенный образ, на котором клиент должен сосредоточиться. Управляемое воображение сочетает в себе два других типа, давая клиенту отправную точку и позволяя клиенту заполнить  пробелы. Управляемое воображение может быть реалистичным или основываться на фантазии или метафорах.',
        'В технике управляемого воображения консультанты используют три типа процессов.  Спонтанное воображение возникает без сознательной направленности содержания. Направленное воображение означает, что консультант предлагает определенный образ, на котором клиент должен сосредоточиться. Управляемое воображение сочетает в себе два других типа, давая клиенту отправную точку и позволяя клиенту заполнить  пробелы. Управляемое воображение может быть реалистичным или основываться на фантазии или метафорах.',
        'Эрфорд Брэдли Т.', '45 основных техник для психологического консультирования', null, -2125843853,
        '2023-11-11 07:46:43.053799', '2023-11-11 07:46:43.053802', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
