        'Эрфорд Брэдли Т.', '45 основных техник для психологического консультирования', null, 755823780,
        '2023-11-11 07:46:43.124239', '2023-11-11 07:46:43.124243', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
