INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('68384ac8-ddfa-4b6a-83ba-1a262c7dffa1',
        'Вполне возможно, что в человеке генетически заложен этот вид борьбы за свободу: подвергаясь действию отрицательных подкрепителей, люди склонны действовать агрессивно и получают положительное подкрепление при виде ущерба, нанесенного своей агрессивностью. Обе тенденции, очевидно, давали эволюционные преимущества, и это можно легко продемонстрировать опытом. Если двум организмам, которые сосуществуют мирно, дать болезненные удары электричеством, то они сразу же начинают характерные агрессивные действия по отношению друг к другу. Агрессивное поведение не обязательно будет направлено на фактический источник отрицательных стимулов; оно может быть "перенесено" на любой другой подходящий объект или человека.',
        'Вполне возможно, что в человеке генетически заложен этот вид борьбы за свободу: подвергаясь действию отрицательных подкрепителей, люди склонны действовать агрессивно и получают положительное подкрепление при виде ущерба, нанесенного своей агрессивностью. Обе тенденции, очевидно, давали эволюционные преимущества, и это можно легко продемонстрировать опытом. Если двум организмам, которые сосуществуют мирно, дать болезненные удары электричеством, то они сразу же начинают характерные агрессивные действия по отношению друг к другу. Агрессивное поведение не обязательно будет направлено на фактический источник отрицательных стимулов; оно может быть "перенесено" на любой другой подходящий объект или человека.',
        'Б. Ф. Скиннер', 'По ту сторону свободы и достоинства', null, -2082112961, '2023-11-11 07:46:55.565516',
        '2023-11-11 07:46:55.565533', 'UNREAD', 50);
