INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('4780d486-56fc-47ba-9c95-f2f4d74456e2',
        'Ошибки перевода сопровождаются ошибками, вытекающими из нашей предвзятости. Мы искажаем других, втискивая их в те идеи и гештальты, которые предпочитаем сами, что прекрасно описано у Пруста:  «Мы заполняем физические очертания существа, которое видим, всеми идеями, которые мы уже выстроили о нём, и в окончательном образе его, который мы создаём в своём уме, эти идеи, конечно, занимают главное место. В конце концов они так плотно прилегают к очертаниям его щёк, так точно следуют за изгибом его носа, так гармонично сочетаются со звуком его голоса, что всё это кажется не более чем прозрачной оболочкой, так что каждый раз, когда мы видим лицо или слышим голос, мы узнаем в нём не что иное, как наши собственные о нём представления».',
        'Ошибки перевода сопровождаются ошибками, вытекающими из нашей предвзятости. Мы искажаем других, втискивая их в те идеи и гештальты, которые предпочитаем сами, что прекрасно описано у Пруста:  «Мы заполняем физические очертания существа, которое видим, всеми идеями, которые мы уже выстроили о нём, и в окончательном образе его, который мы создаём в своём уме, эти идеи, конечно, занимают главное место. В конце концов они так плотно прилегают к очертаниям его щёк, так точно следуют за изгибом его носа, так гармонично сочетаются со звуком его голоса, что всё это кажется не более чем прозрачной оболочкой, так что каждый раз, когда мы видим лицо или слышим голос, мы узнаем в нём не что иное, как наши собственные о нём представления».',
        'Ирвин Ялом', 'Палач любви', null, -503262948, '2023-11-11 07:47:00.877134', '2023-11-11 07:47:00.877135',
        'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('20057488-122c-48a9-a010-fa70dd823e1a',
        'Во-первых, существует барьер между образом и языком. Сознание мыслит образами, но для общения с другими приходится трансформировать образы в мысли, а затем мысли в слова. Этот путь: от образа к мысли и языку, очень коварен. Происходят потери: богатая, сочная ткань образа, его необыкновенная пластичность и текучесть, его личные ностальгические эмоциональные оттенки — всё утрачивается, когда образ втискивают в язык.  Великие мастера пытаются передать образ непосредственно с помощью намёков, метафор, с помощью лингвистических приёмов, направленных на то, чтобы вызвать у читателя похожий образ. Но в конце концов они понимают несоразмерность своих средств стоящей перед ними задаче. Послушайте жалобу Флобера в «Мадам Бовари»:  «Как будто полнота души не изливается подчас в пустопорожних метафорах! Ведь никто же до сих пор не сумел найти точные слова для выражения своих чаяний, замыслов, горестей, ибо человеческая речь подобна треснутому котлу, и когда нам хочется растрогать своей музыкой звезды, у нас получается собачий вальс».',
