        'Дарио Салас Соммэр', 'Существует ли женщина?', null, 619659011, '2023-11-11 07:47:04.813848',
        '2023-11-11 07:47:04.813850', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
