INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('11072137-2e2f-4d93-a8f7-1613072f31aa',
        'Сложность жизни человека в ее непредопределенности. То, от чего человек страдает, – это невозможность занять место в собственном существовании, в собственной экзистенции. Человеку вечно не хватает места в собственной жизни, и именно поэтому психоанализ так хорошо подходит тем, кто ощутил это странное гнетущее чувство: на кушетке аналитика речь идет только о том, что касается бытия анализанта, и ничего более. Но все же, какое место занимает человек или, вернее сказать, посредством чего? Для ответа на этот вопрос обратимся к логике трех регистров – Символического, Воображаемого и Реального, в рамках которых протекает жизнь субъекта.  С измерением Воображаемого мы уже сталкивались, когда говорили о буквально «визуальном» присвоении себе зеркального образа в младенчестве;  «воображаемое» – это пространство взгляда, пространство очевидного, пространство «видимого» нами. Функция Воображаемого заключается в ответе на вопрос «какой я?» и «какой ты?».',
        'Сложность жизни человека в ее непредопределенности. То, от чего человек страдает, – это невозможность занять место в собственном существовании, в собственной экзистенции. Человеку вечно не хватает места в собственной жизни, и именно поэтому психоанализ так хорошо подходит тем, кто ощутил это странное гнетущее чувство: на кушетке аналитика речь идет только о том, что касается бытия анализанта, и ничего более. Но все же, какое место занимает человек или, вернее сказать, посредством чего? Для ответа на этот вопрос обратимся к логике трех регистров – Символического, Воображаемого и Реального, в рамках которых протекает жизнь субъекта.  С измерением Воображаемого мы уже сталкивались, когда говорили о буквально «визуальном» присвоении себе зеркального образа в младенчестве;  «воображаемое» – это пространство взгляда, пространство очевидного, пространство «видимого» нами. Функция Воображаемого заключается в ответе на вопрос «какой я?» и «какой ты?».',
        'Дмитрий Лобачёв', 'История французского психоанализа в лицах', null, -977057011, '2023-11-11 07:47:12.704890',
        '2023-11-11 07:47:12.704897', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('2a95f5c5-039c-47d5-b4e3-b395f480a03e',
        'Один из величайших жизненных парадоксов заключается в том, что развитие самосознания усиливает тревогу. Слияние рассеивает тревогу самым радикальным образом – уничтожая самосознание.  Человек, который влюбляется и переживает блаженное состояние слияния с любимым, не рефлексирует, поскольку его одинокое сомневающееся «Я» и сопутствующий страх изоляции растворяются в «мы». Таким образом, человек избавляется от тревоги, теряя самого себя.',
        'Один из величайших жизненных парадоксов заключается в том, что развитие самосознания усиливает тревогу. Слияние рассеивает тревогу самым радикальным образом – уничтожая самосознание.  Человек, который влюбляется и переживает блаженное состояние слияния с любимым, не рефлексирует, поскольку его одинокое сомневающееся «Я» и сопутствующий страх изоляции растворяются в «мы». Таким образом, человек избавляется от тревоги, теряя самого себя.',
        'Ирвин Ялом', 'Палач любви и другие психотерапевтические истории', null, -1928099545,
        '2023-11-11 07:47:10.768788', '2023-11-11 07:47:10.768801', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('b3475b38-04bf-4902-ac7a-7efd9b1128c5',
        '"Психика - функция мозга. Сама психическая деятельность как высшая нервная деятельность имеет и свою физиологическую, нейродинамическую характеристику. Такую характеристику имеет вместе с тем в каждый данный момент и всякая морфологическая структура мозга, поскольку мозг - не мертвая вещь, кусок неживой природы, а функционирующий, работающий орган. В этой нейродинамической характеристике морфологическая структура и физиологическая функция мозга смыкаются, сливаются, совпадают, так что не приходится внешне их соотносить. Происходит в известном смысле "слитие", объединение физиологии и морфологии; функция и структура объединяются в "конструкции". Это объединение основывается не только на зависимости функции от структуры, но и на том, что образующиеся в процессе функционирования связи откладываются в структуре, что формирование структуры само обусловлено функцией".',
        '"Психика - функция мозга. Сама психическая деятельность как высшая нервная деятельность имеет и свою физиологическую, нейродинамическую характеристику. Такую характеристику имеет вместе с тем в каждый данный момент и всякая морфологическая структура мозга, поскольку мозг - не мертвая вещь, кусок неживой природы, а функционирующий, работающий орган. В этой нейродинамической характеристике морфологическая структура и физиологическая функция мозга смыкаются, сливаются, совпадают, так что не приходится внешне их соотносить. Происходит в известном смысле "слитие", объединение физиологии и морфологии; функция и структура объединяются в "конструкции". Это объединение основывается не только на зависимости функции от структуры, но и на том, что образующиеся в процессе функционирования связи откладываются в структуре, что формирование структуры само обусловлено функцией".',
        'С. Л. Рубинштейн', 'Бытие и сознание', null, 825829409, '2023-11-11 07:47:10.781214',
        '2023-11-11 07:47:10.781217', 'UNREAD', 50);
