INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('61f24255-152c-4e3d-8a0a-a69ba1ea24f5',
        'При “разметке минного поля” решающее значение для процесса имеет распознавание предупреждающих знаков и потенциальных ловушек. Упреждающее выявление проблем и разработка плана также являются важными компонентами долгосрочных изменений.',
