INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('fedc7a26-a71b-4ae4-a551-8b8d1c2baefb',
        'Пресбери, Эчтерлинг и Макки [Presbury, Echterling &amp; McKee, 2002] утверждают: “Поиск исключений является наиболее важной техникой консультирования для разрешения проблем. Исключение — время, когда проблема не возникает — это решение, которого клиент уже достиг, пусть и временно” (р. 75). Истоки техники исключений лежат  в предположении, что все проблемы имеют исключения, которые можно использовать для поиска решений. Люди иногда рассматривают свои проблемы так, будто они возникали всегда, как постоянные и непрекращающиеся даже на короткие мгновения. Если мы признаем исключения из проблемы, значит, мы склонны отрицать их значимость.',
        'Пресбери, Эчтерлинг и Макки [Presbury, Echterling &amp; McKee, 2002] утверждают: “Поиск исключений является наиболее важной техникой консультирования для разрешения проблем. Исключение — время, когда проблема не возникает — это решение, которого клиент уже достиг, пусть и временно” (р. 75). Истоки техники исключений лежат  в предположении, что все проблемы имеют исключения, которые можно использовать для поиска решений. Люди иногда рассматривают свои проблемы так, будто они возникали всегда, как постоянные и непрекращающиеся даже на короткие мгновения. Если мы признаем исключения из проблемы, значит, мы склонны отрицать их значимость.',
        'Эрфорд Брэдли Т.', '45 основных техник для психологического консультирования', null, -328067312,
        '2023-11-11 07:47:12.669875', '2023-11-11 07:47:12.669888', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('bb7d56e3-075e-4f2e-871f-067c85d29d0e',
        'В 1975 году выходит еще одна небезынтересная работа Леклера «Убийство ребенка». Мрачное название не должно отпугнуть читателя, ведь главная мысль этой книги – чтобы обрести максимально полное соответствие самому себе, мы должны постоянно уничтожать, деконструировать, «убивать воображаемый образ самих себя, внушаемый нам нашими родителями. Мы все должны бороться с тем, что автор называет первичным нарциссизмом, проекцией ребенка, которого хотели наши родители. Смысл заключается в том, что каждый из нас несет в себе бремя бессознательных тайн наших родителей, скрытое желание, которое мы вынуждены проживать». Проще говоря, мы должны умереть (образно убив родительское о себе представление), чтобы родиться, но уже самими собой.',
        'В 1975 году выходит еще одна небезынтересная работа Леклера «Убийство ребенка». Мрачное название не должно отпугнуть читателя, ведь главная мысль этой книги – чтобы обрести максимально полное соответствие самому себе, мы должны постоянно уничтожать, деконструировать, «убивать воображаемый образ самих себя, внушаемый нам нашими родителями. Мы все должны бороться с тем, что автор называет первичным нарциссизмом, проекцией ребенка, которого хотели наши родители. Смысл заключается в том, что каждый из нас несет в себе бремя бессознательных тайн наших родителей, скрытое желание, которое мы вынуждены проживать». Проще говоря, мы должны умереть (образно убив родительское о себе представление), чтобы родиться, но уже самими собой.',
        'Дмитрий Лобачёв', 'История французского психоанализа в лицах', null, -1520320056, '2023-11-11 07:47:12.681316',
        '2023-11-11 07:47:12.681326', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
