INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('d07d5135-0fff-4a23-9284-1b1268ca5f66',
        'Бессознательное проявляет себя посредством языковых феноменов, таких как речь, оговорка, описка, сновидение, свободные ассоциации и, наконец, симптом! Лакан говорит, что в этом открытии – возможности перевода, перевода-расшифровки – и располагается гений Фрейда: «Да, Фрейд совершил поистине гениальный ход и сделал это отнюдь не по наитию – ход лингвиста, который, заметив, что в тексте повторяется определенный знак, предположил, что за этим что-то стоит, и реконструировал значения остальных знаков этого языка». Соответственно, язык – не просто инструмент: он является сущностью человека; иначе говоря, сам человек определен языком, и его бессознательное представляет собой «ту часть конкретного трансиндивидуального дискурса, которой не хватает субъекту для восстановления непрерывности своего сознательного дискурса».  Лакан говорил: « Бессознательное — это та часть моей истории, которая содержит белое пятно, или ложь: это глава, прошедшая цензуру».',
        'Бессознательное проявляет себя посредством языковых феноменов, таких как речь, оговорка, описка, сновидение, свободные ассоциации и, наконец, симптом! Лакан говорит, что в этом открытии – возможности перевода, перевода-расшифровки – и располагается гений Фрейда: «Да, Фрейд совершил поистине гениальный ход и сделал это отнюдь не по наитию – ход лингвиста, который, заметив, что в тексте повторяется определенный знак, предположил, что за этим что-то стоит, и реконструировал значения остальных знаков этого языка». Соответственно, язык – не просто инструмент: он является сущностью человека; иначе говоря, сам человек определен языком, и его бессознательное представляет собой «ту часть конкретного трансиндивидуального дискурса, которой не хватает субъекту для восстановления непрерывности своего сознательного дискурса».  Лакан говорил: « Бессознательное — это та часть моей истории, которая содержит белое пятно, или ложь: это глава, прошедшая цензуру».',
        'Дмитрий Лобачёв', 'История французского психоанализа в лицах', null, 26851760, '2023-11-11 07:47:12.696449',
        '2023-11-11 07:47:12.696453', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
