VALUES ('b1dd3325-458b-4a1e-b22d-4e85bbf98281',
        'Библиотерапия может быть неэффективной, если у участников наблюдается “отсутствие социальных и эмоциональных переживаний, срывы, полеты фантазии и оборонительные действия” [ Gladding &amp; Gladding, 1991, р. 9]. Клиенты могут быть не готовы измениться или не захотеть использовать эту технику. Другим ограничением библиотерапии может быть то, что материалы по данной теме могут быть недоступными [ Abdullah, 2002].',
        'Библиотерапия может быть неэффективной, если у участников наблюдается “отсутствие социальных и эмоциональных переживаний, срывы, полеты фантазии и оборонительные действия” [ Gladding &amp; Gladding, 1991, р. 9]. Клиенты могут быть не готовы измениться или не захотеть использовать эту технику. Другим ограничением библиотерапии может быть то, что материалы по данной теме могут быть недоступными [ Abdullah, 2002].',
        'Эрфорд Брэдли Т.', '45 основных техник для психологического консультирования', null, 1250788797,
        '2023-11-11 07:47:16.340085', '2023-11-11 07:47:16.340090', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('d12596f4-913d-4d2e-8bd3-48573a459ca4',
        'Наконец, в работе [ Moldovan, Cobeanu &amp; David, 2013] было показано, что когнитивная библиотерапия уменьшает депрессивные симптомы и когнитивные способности как при прекращении лечения, так и при последующем наблюдении по сравнению с плацебо, отсроченным лечением и группами без лечения. Также было установлено, что автоматические мысли значительно опосредовали влияние библиотерапии на депрессивные симптомы.',
        'Наконец, в работе [ Moldovan, Cobeanu &amp; David, 2013] было показано, что когнитивная библиотерапия уменьшает депрессивные симптомы и когнитивные способности как при прекращении лечения, так и при последующем наблюдении по сравнению с плацебо, отсроченным лечением и группами без лечения. Также было установлено, что автоматические мысли значительно опосредовали влияние библиотерапии на депрессивные симптомы.',
        'Эрфорд Брэдли Т.', '45 основных техник для психологического консультирования', null, 1377701333,
        '2023-11-11 07:47:16.361060', '2023-11-11 07:47:16.361068', 'UNREAD', 50);
