INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('89245c04-dc2e-4032-897d-7fc4f7e37fa2',
        'Джонсон и соавторы описали пятиэтапный процесс реализации библиотерапии в классе: 1) мотивировать учеников вводной деятельностью; 2) предоставить время для чтения; 3) предоставить время для размышлений; 4) участвовать в последующем обсуждении; и 5) заканчивать обсуждение заключением и оценкой.',
        'Джонсон и соавторы описали пятиэтапный процесс реализации библиотерапии в классе: 1) мотивировать учеников вводной деятельностью; 2) предоставить время для чтения; 3) предоставить время для размышлений; 4) участвовать в последующем обсуждении; и 5) заканчивать обсуждение заключением и оценкой.',
        'Эрфорд Брэдли Т.', '45 основных техник для психологического консультирования', null, -1122481596,
        '2023-11-11 07:47:16.384889', '2023-11-11 07:47:16.384891', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('67182700-c4dc-4eef-a174-0416df684aaf',
        'Существует множество вариантов библиотерапии. Традиционная  библиотерапия, как описано выше, имеет тенденцию быть реактивной  по своей природе, то есть у клиента есть проблема, и профессиональный консультант выбирает книгу для чтения клиентом, которая поможет решить эту проблему. Интерактивная библиотерапия предполагает участие клиентов таким образом, чтобы они могли размышлять о прочитанном. Способы, которыми профессиональные консультанты привлекают клиентов, различаются, но они могут включать групповое  обсуждение или ведение дневника. Клиническая библиотерапия используется только обученными профессиональными консультантами, чтобы помочь клиентам, у которых возникли серьезные эмоциональные проблемы [Abdullah, 2002]. Она может предусматривать ведение дневника, ролевые игры или рисование. Когнитивная библиотерапия используется для обучения когнитивно-поведенческой терапии клиентов, страдающих депрессией, с целью снижения уровня депрессии  [ Gregory, Canning, Lee &amp; Wise, 2004]. Учителя обычно используют развивающую библиотерапию со своими учениками во время групповых занятий или обучения грамоте, помогая поддерживать нормальное здоровье [Abdullah, 2002].',
        'Существует множество вариантов библиотерапии. Традиционная  библиотерапия, как описано выше, имеет тенденцию быть реактивной  по своей природе, то есть у клиента есть проблема, и профессиональный консультант выбирает книгу для чтения клиентом, которая поможет решить эту проблему. Интерактивная библиотерапия предполагает участие клиентов таким образом, чтобы они могли размышлять о прочитанном. Способы, которыми профессиональные консультанты привлекают клиентов, различаются, но они могут включать групповое  обсуждение или ведение дневника. Клиническая библиотерапия используется только обученными профессиональными консультантами, чтобы помочь клиентам, у которых возникли серьезные эмоциональные проблемы [Abdullah, 2002]. Она может предусматривать ведение дневника, ролевые игры или рисование. Когнитивная библиотерапия используется для обучения когнитивно-поведенческой терапии клиентов, страдающих депрессией, с целью снижения уровня депрессии  [ Gregory, Canning, Lee &amp; Wise, 2004]. Учителя обычно используют развивающую библиотерапию со своими учениками во время групповых занятий или обучения грамоте, помогая поддерживать нормальное здоровье [Abdullah, 2002].',
        'Эрфорд Брэдли Т.', '45 основных техник для психологического консультирования', null, 1005690384,
        '2023-11-11 07:47:16.390438', '2023-11-11 07:47:16.390445', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
