VALUES ('7a74275e-cf20-440a-9452-b54eac84e692',
        'В практических целях важно отличать чувство – как рациональную, оценивающую функцию – от эмоции, которая исходит из активизированного комплекса.',
        'В практических целях важно отличать чувство – как рациональную, оценивающую функцию – от эмоции, которая исходит из активизированного комплекса.',
        'Мэрион Вудман', 'Сова была раньше дочкой пекаря. Ожирение, нервная анорексия и подавленная женственность',
        null, -1969214462, '2023-11-11 07:47:21.936003', '2023-11-11 07:47:21.936008', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('360325b9-2399-4c9c-8158-40ffd38933c8',
        'Отождествленность с телом позволяет человеку избежать обмана слов. Она обеспечивает эго фундаментом реальности. Опасность языка заключается в его способности околдовывать неосторожных. Кто неосторожен? Быть неосторожным —значит не сознавать; быть неосторожным, это значит не контактировать со своим собственным телом. Область чувств недоступна неосторожному человеку —состояние, которое вызывает у него тревогу, заволокли его суждения, делающие его приверженцем слов.',
        'Отождествленность с телом позволяет человеку избежать обмана слов. Она обеспечивает эго фундаментом реальности. Опасность языка заключается в его способности околдовывать неосторожных. Кто неосторожен? Быть неосторожным —значит не сознавать; быть неосторожным, это значит не контактировать со своим собственным телом. Область чувств недоступна неосторожному человеку —состояние, которое вызывает у него тревогу, заволокли его суждения, делающие его приверженцем слов.',
        'Александр Лоуэн', 'Предательство тела', null, -2082969171, '2023-11-11 07:47:21.940663',
        '2023-11-11 07:47:21.940665', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
