INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('b3996849-b42e-40a4-bf4b-9b38d0ef9e58',
        'Чувства не подчиняются рациональным законам причин и следствий. Поэтому наука их не исследует. Они относятся к сфере общности, а не причинности. На чувства влияют другие чувства, действие для этого совершенно не обязательно. Счастливый человек поднимает настроение у окружающих, хотя не делает ничего особенного, чтобы этого добиться. Мрачный человек действует подавляюще, даже если не произносит ни слова. Чувства как инфекция, они распространяются в континууме и охватывают всех, кто находится в радиусе их действия.',
        'Чувства не подчиняются рациональным законам причин и следствий. Поэтому наука их не исследует. Они относятся к сфере общности, а не причинности. На чувства влияют другие чувства, действие для этого совершенно не обязательно. Счастливый человек поднимает настроение у окружающих, хотя не делает ничего особенного, чтобы этого добиться. Мрачный человек действует подавляюще, даже если не произносит ни слова. Чувства как инфекция, они распространяются в континууме и охватывают всех, кто находится в радиусе их действия.',
        'Александр Лоуэн', 'Предательство тела', null, 1267559306, '2023-11-11 07:47:21.966991',
        '2023-11-11 07:47:21.967001', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('59a82f1b-8dd6-4618-924a-109cb1281d84',
        'Печаль, злость и страх (в отличие от вины и стыда) можно назвать эмоциями восприятия, поскольку эти чувства прямо воспринимает эго и не выносит по их поводу оценочных суждений. Если прямо воспринимаемые чувства рассматриваются эго как плохие или хорошие, они утрачивают свою биологическую ценность и приобретают моральную или концептуальную оценку. Это порождает ситуацию, в которой самопринятие очень затрудняется или становится невозможным.',
        'Печаль, злость и страх (в отличие от вины и стыда) можно назвать эмоциями восприятия, поскольку эти чувства прямо воспринимает эго и не выносит по их поводу оценочных суждений. Если прямо воспринимаемые чувства рассматриваются эго как плохие или хорошие, они утрачивают свою биологическую ценность и приобретают моральную или концептуальную оценку. Это порождает ситуацию, в которой самопринятие очень затрудняется или становится невозможным.',
        'Александр Лоуэн', 'Предательство тела', null, 51704984, '2023-11-11 07:47:21.973065',
        '2023-11-11 07:47:21.973068', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('628cddb4-a8c5-405b-ab9b-eb550365d5ec',
        'Человек отвечает за свои действия, а за свои чувства он отвечать не может. Чувства — это биологическая реакция тела, которая находится за пределами диктата эго. Роль эго состоит в том, чтобы понять чувство, а не судить о нем и не контролировать его. Ему подконтрольны действия. Здоровый человек, который злится или чувствует сексуальное возбуждение, способен контейнировать свое чувство до тех пор, пока не найдет подходящего случая и не выразит его. Это — ответственное поведение. Здоровое эго не беспомощно в отношениях с телом. Если экспрессия чувства в словах или делах может помешать, эго способно удержать эту экспрессию, контролируя произвольную мускулатуру, и в то же время не отрицать и не вытеснять чувство. Вреда можно избежать, не создавая внутреннего конфликта. Когда человек считает свое чувство «плохим», он подавляет его и осуждает себя за то, что переживает его. В результате, действия не совпадают с исходным чувством, а отражают вину или самоосуждение. Подавление и вытеснение чувства приводит к утрате самовосприятия, что ослабляет эго и подрывает его способность действовать ответственно.',
        'Человек отвечает за свои действия, а за свои чувства он отвечать не может. Чувства — это биологическая реакция тела, которая находится за пределами диктата эго. Роль эго состоит в том, чтобы понять чувство, а не судить о нем и не контролировать его. Ему подконтрольны действия. Здоровый человек, который злится или чувствует сексуальное возбуждение, способен контейнировать свое чувство до тех пор, пока не найдет подходящего случая и не выразит его. Это — ответственное поведение. Здоровое эго не беспомощно в отношениях с телом. Если экспрессия чувства в словах или делах может помешать, эго способно удержать эту экспрессию, контролируя произвольную мускулатуру, и в то же время не отрицать и не вытеснять чувство. Вреда можно избежать, не создавая внутреннего конфликта. Когда человек считает свое чувство «плохим», он подавляет его и осуждает себя за то, что переживает его. В результате, действия не совпадают с исходным чувством, а отражают вину или самоосуждение. Подавление и вытеснение чувства приводит к утрате самовосприятия, что ослабляет эго и подрывает его способность действовать ответственно.',
        'Александр Лоуэн', 'Предательство тела', null, 1469386485, '2023-11-11 07:47:21.976761',
        '2023-11-11 07:47:21.976762', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
