INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('ec1777c7-dce0-4d54-bbc0-a7b8ad17fbc0',
        '«Если вы подойдете ко мне со сжатыми кулаками, – говорил Вудро Вильсон, – то могу, думаю, обещать вам, что и мои кулаки сожмутся в ответ. Но если вы придете ко мне со словами: "Давайте сядем друг против друга и постараемся разобраться, в чем мы расходимся и почему", – то мы, скорее всего, очень быстро обнаружим, что наши мнения расходятся не так уж и сильно, что спорных моментов совсем немного, а таких, по которым наши точки зрения совпадают, гораздо больше, и, если только у нас хватит терпения, объективности и желания, мы придем к соглашению».',
