INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('ac67a6bc-e6c3-4ef9-89d0-49c9955d206a',
        'Попытаемся вслед за Eisenberg и Delaney (1977) указать на важнейшие цели первой консультативной встречи:1. Поощрять открытое, искреннее и всеобъемлющее общение по проблемам, которые клиенту хочется обсудить во время встречи, с раскрытием факторов, вызывающих эти проблемы.2. Продвинуться в направлении более глубокого понимания клиента, большего уважения к нему и взаимопонимания.3. Настроить клиента на конкретную полезность каждой консультативной встречи.4. Передать клиенту понимание того, что он должен быть активным участником решения своих проблем.5. Обозначить проблемы для дальнейшей работы.',
        'Попытаемся вслед за Eisenberg и Delaney (1977) указать на важнейшие цели первой консультативной встречи:1. Поощрять открытое, искреннее и всеобъемлющее общение по проблемам, которые клиенту хочется обсудить во время встречи, с раскрытием факторов, вызывающих эти проблемы.2. Продвинуться в направлении более глубокого понимания клиента, большего уважения к нему и взаимопонимания.3. Настроить клиента на конкретную полезность каждой консультативной встречи.4. Передать клиенту понимание того, что он должен быть активным участником решения своих проблем.5. Обозначить проблемы для дальнейшей работы.',
        'Р. Кочюнас', 'Основы психологического консультирования', null, 2056027232, '2023-11-11 07:47:27.559146',
        '2023-11-11 07:47:27.559148', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('ed93c899-38e1-4d44-bca8-040d5844db78',
        'Первые вопросы следует тщательно продумывать, потому что клиенты в начале консультирования не осмеливаются отвечать на некоторые вопросы. Если консультант задает много вопросов, клиент перестает понимать, что от него хотят, и не говорит по собственной инициативе, и тогда консультант вынужден продолжать спрашивать. В начале консультирования следует избегать вопросов, на которые возможен однозначный ответ, ибо это также заставляет консультанта продолжать спрашивать (например, вопрос: "Сколько Вам лет?" иногда важен, но неприемлем в начале консультирования).',
