        'Р. Кочюнас', 'Основы психологического консультирования', null, -1524635087, '2023-11-11 07:47:27.562038',
        '2023-11-11 07:47:27.562039', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
