        'Александр Лоуэн', 'Предательство тела', null, -1139403729, '2023-11-11 07:47:29.699497',
        '2023-11-11 07:47:29.699509', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
