        'Р. Кочюнас', 'Основы психологического консультирования', null, -436631659, '2023-11-11 07:47:29.766959',
        '2023-11-11 07:47:29.766964', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
