INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('26837572-366f-4b08-9ccd-5b3c993d6c79',
        'Консультирование — это профессиональное отношение  квалифицированного консультанта к клиенту, которое обычно представляется как  "личность-личность", хотя иногда в нем участвуют более двух человек. Цель  консультирования — помочь клиентам понять происходящее в их жизненном  пространстве и осмысленно достичь поставленной цели на основе осознанного  выбора при разрешении проблем эмоционального и межличностного характера.',
        'Консультирование — это профессиональное отношение  квалифицированного консультанта к клиенту, которое обычно представляется как  "личность-личность", хотя иногда в нем участвуют более двух человек. Цель  консультирования — помочь клиентам понять происходящее в их жизненном  пространстве и осмысленно достичь поставленной цели на основе осознанного  выбора при разрешении проблем эмоционального и межличностного характера.',
        'Р. Кочюнас', 'Основы психологического консультирования', null, -691047741, '2023-11-11 07:47:31.681996',
        '2023-11-11 07:47:31.681998', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('89a8291f-1c20-49f1-8ca4-3096c5efd2a1',
        'Мне бы не хотелось создать впечатление, что все толстые люди — параноики. Переедание — это один из обычных способов «отыгрывания» фрустрации, которая возникла потому, что человек не смог найти удовлетворения на взрослом уровне. Существуют и другие способы такого «отыгрывания» фрустрации: бунт, расовые предрассудки, сексуальное распутство, выпивка и т.д. «Отыгрывание» — это паранойяльный механизм, который в какой-либо степени присутствует укаждого шизоидного индивидуума. Я описал только «компульсивного обжору», который не может отказаться от пищи, живет головой, и чья единственная связь с собственным телом проходит по кишечному тракту. Противоположность компульсивного обжоры представляет человек, который носится с идеей стройности. Конечно, стройным быть модно. Отчасти, эту моду можно объяснить как реакцию на переедание, которое стало характерной чертой нашего «чрезмерно тучного общества», а отчасти — интенсивной компульсивной борьбой, которая превращает жизнь в скачки. В этой скачке толстые не выдерживают.',
        'Мне бы не хотелось создать впечатление, что все толстые люди — параноики. Переедание — это один из обычных способов «отыгрывания» фрустрации, которая возникла потому, что человек не смог найти удовлетворения на взрослом уровне. Существуют и другие способы такого «отыгрывания» фрустрации: бунт, расовые предрассудки, сексуальное распутство, выпивка и т.д. «Отыгрывание» — это паранойяльный механизм, который в какой-либо степени присутствует укаждого шизоидного индивидуума. Я описал только «компульсивного обжору», который не может отказаться от пищи, живет головой, и чья единственная связь с собственным телом проходит по кишечному тракту. Противоположность компульсивного обжоры представляет человек, который носится с идеей стройности. Конечно, стройным быть модно. Отчасти, эту моду можно объяснить как реакцию на переедание, которое стало характерной чертой нашего «чрезмерно тучного общества», а отчасти — интенсивной компульсивной борьбой, которая превращает жизнь в скачки. В этой скачке толстые не выдерживают.',
        'Александр Лоуэн', 'Предательство тела', null, 1701188392, '2023-11-11 07:47:33.522119',
        '2023-11-11 07:47:33.522126', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('e0d011c2-cad9-4ca2-9d64-55b06c9bc5a9',
        'Слово «астения», от которого происходит термин «астеничный», означает слабость и расслабленность. В каждом шизоиде, независимо от типа его тела, присутствует некая фундаментальная слабость. Это — неспособность мобилизовать энергию и чувства и направить их на удовлетворение своих потребностей. Невзирая на видимость силы, шизоиду не хватает энергии, чтобы поддерживать агрессивную позицию по отношению к миру.',
        'Слово «астения», от которого происходит термин «астеничный», означает слабость и расслабленность. В каждом шизоиде, независимо от типа его тела, присутствует некая фундаментальная слабость. Это — неспособность мобилизовать энергию и чувства и направить их на удовлетворение своих потребностей. Невзирая на видимость силы, шизоиду не хватает энергии, чтобы поддерживать агрессивную позицию по отношению к миру.',
