INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('76b7b2f8-9ca3-400b-a170-bc6e839b42b4',
        'Удовлетворяющее сексуальное переживание, конечно, наиболее естественное и сильное снотворное. Здоровый человек засыпает немедленно после оргазма, если половое взаимодействие произошло в ночное время. Сон, который следует за сексуальным взаимодействием, обычно бывает глубоким и освежающим переживанием. Приятныйсекс приносит такой результат, потому что приводит человека к более тесному контакту с собственным телом, принося чувства в его нижнюю часть. Удовлетворяющая мастурбация действует также, особенно на молодых людей. В раннем детстве кормление грудью функционирует таким же образом, убаюкивая ум приятным чувством тела. Насытившийся ребенок легко скатывается в сон, во время которого сосок материнской груди остается у него во рту, создавая ощущение безопасности и близости матери. Во взрослом состоянии чувство безопасности и тепла, которое ребенок вынес благодаря близости к матери, обеспечивает приятным контактом с собственным телом. Поскольку физическая активность дает такой контакт, она часто фасилитирует засыпание. Я предлагал пациентам выполнять несколько простых физических упражнений перед тем, как лечь спать, чтобы они смогли преодолеть свою зависимость от седативных средств. В таких упражнениях очень важно глубоко и ровно дышать, что релаксирует тело и наполняет его приятными ощущениями. В большинстве случаев эта простая процедура позволяла пациенту легко заснуть. Очень часто бывает достаточно одного только дыхания.',
        'Удовлетворяющее сексуальное переживание, конечно, наиболее естественное и сильное снотворное. Здоровый человек засыпает немедленно после оргазма, если половое взаимодействие произошло в ночное время. Сон, который следует за сексуальным взаимодействием, обычно бывает глубоким и освежающим переживанием. Приятныйсекс приносит такой результат, потому что приводит человека к более тесному контакту с собственным телом, принося чувства в его нижнюю часть. Удовлетворяющая мастурбация действует также, особенно на молодых людей. В раннем детстве кормление грудью функционирует таким же образом, убаюкивая ум приятным чувством тела. Насытившийся ребенок легко скатывается в сон, во время которого сосок материнской груди остается у него во рту, создавая ощущение безопасности и близости матери. Во взрослом состоянии чувство безопасности и тепла, которое ребенок вынес благодаря близости к матери, обеспечивает приятным контактом с собственным телом. Поскольку физическая активность дает такой контакт, она часто фасилитирует засыпание. Я предлагал пациентам выполнять несколько простых физических упражнений перед тем, как лечь спать, чтобы они смогли преодолеть свою зависимость от седативных средств. В таких упражнениях очень важно глубоко и ровно дышать, что релаксирует тело и наполняет его приятными ощущениями. В большинстве случаев эта простая процедура позволяла пациенту легко заснуть. Очень часто бывает достаточно одного только дыхания.',
        'Александр Лоуэн', 'Предательство тела', null, 320996128, '2023-11-11 07:47:31.759803',
        '2023-11-11 07:47:31.759804', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
