INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('03c2fe89-28f7-4c28-96cf-17a6338ab910',
        'Чувство отождествленности основано на сознавании желания, понимании потребности и восприятии телесных ощущений. Когда пациент говорит: «Я незнаю, кто я», — он, фактически, сообщает следующее: «Я не знаю, что я чувствую, чего я хочу и в чем нуждаюсь». Он знает, что ему требуется помощь, но этим и ограничено его самосознавание (self-awereness), в товремя как отождествление остается неясным и туманным. Он не сознает, что злится или грустит, что его тело сковано мышечными напряжениями и что он не может любить и чувствовать удовольствие. Несмотря на то, что на каком-то глубоком уровне сознания он может понимать эти факты, он все же не способен сделать их личностным переживанием.',
        'Чувство отождествленности основано на сознавании желания, понимании потребности и восприятии телесных ощущений. Когда пациент говорит: «Я незнаю, кто я», — он, фактически, сообщает следующее: «Я не знаю, что я чувствую, чего я хочу и в чем нуждаюсь». Он знает, что ему требуется помощь, но этим и ограничено его самосознавание (self-awereness), в товремя как отождествление остается неясным и туманным. Он не сознает, что злится или грустит, что его тело сковано мышечными напряжениями и что он не может любить и чувствовать удовольствие. Несмотря на то, что на каком-то глубоком уровне сознания он может понимать эти факты, он все же не способен сделать их личностным переживанием.',
        'Александр Лоуэн', 'Предательство тела', null, 562945733, '2023-11-11 07:47:29.667627',
        '2023-11-11 07:47:29.667629', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('926d1f9d-370a-4c72-b47c-ae42d98069e0',
        'Проблема переедания связана с отсутствием настоящего чувства удовольствия. Страдающие компульсивным обжорством пациенты, неизбежно замечают, что не чувствуют истинного удовольствия от еды. Когда к человеку возвращается право и способность чувствовать удовольствие, его обжорство автоматически сходит на нет. Прием пищи, основанный на принципе удовольствия, сам по себе становится удовольствием, он несовместим с компульсивностью.',
        'Проблема переедания связана с отсутствием настоящего чувства удовольствия. Страдающие компульсивным обжорством пациенты, неизбежно замечают, что не чувствуют истинного удовольствия от еды. Когда к человеку возвращается право и способность чувствовать удовольствие, его обжорство автоматически сходит на нет. Прием пищи, основанный на принципе удовольствия, сам по себе становится удовольствием, он несовместим с компульсивностью.',
        'Александр Лоуэн', 'Предательство тела', null, 1743547846, '2023-11-11 07:47:33.531665',
