INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('43332f24-8d61-469c-a556-8bf298ac14ca',
        'Каждый раз, когда она ела больше, чем необходимо, она чувствовала себя виноватой и приходила в отчаяние. Еда не приносила настоящего удовольствия, она только позволяла почувствовать себя лучше, поскольку «ослабляла напряжение». Моя пациентка никогда не была голодна, и я очень сомневаюсь в том, что она боялась почувствовать голод — по крайней мере голод, связанный с пищей. В глубоком смысле она, конечно, была голодна: она голодала по любви, по удовольствию, голодала по жизни. Она охотно прекратила бы есть, если бы верила, что может удовлетворить другие свои потребности. Компульсивное переедание было  признаком отчаяния.',
        'Каждый раз, когда она ела больше, чем необходимо, она чувствовала себя виноватой и приходила в отчаяние. Еда не приносила настоящего удовольствия, она только позволяла почувствовать себя лучше, поскольку «ослабляла напряжение». Моя пациентка никогда не была голодна, и я очень сомневаюсь в том, что она боялась почувствовать голод — по крайней мере голод, связанный с пищей. В глубоком смысле она, конечно, была голодна: она голодала по любви, по удовольствию, голодала по жизни. Она охотно прекратила бы есть, если бы верила, что может удовлетворить другие свои потребности. Компульсивное переедание было  признаком отчаяния.',
        'Александр Лоуэн', 'Предательство тела', null, 774760539, '2023-11-11 07:47:33.537616',
