INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('79efa4ce-4758-46f0-aaea-f23b076c009a',
        'Как мы уже отметили, некоторые традиции утверждают, чтоКосмический человек является целью мироздания, но достижение этой цели невозможно понять как внешнее событие. С точки зрения индусов, например, дело не в том, что внешний мир однажды растворится в первоначальном Великом человеке, а в том, что экстравертная ориентация эго на внешний мир исчезнет, уступив путь Космическому человеку. Это произойдет, когда эго сольется с самостью. Широкий поток проявлений эго (идущий от одной мысли к другой) и его желаний (которые бегут от одного объекта к другому) затихнет в момент, когда внутри будет обретен Великий человек. Действительно, мы никогда не должны забывать, что внешняя реальность для нас существует лишь до тех пор, пока мы ее сознательно воспринимаем, и что мы не можем доказать ее существования «в себе и самой по себе».',
        'Как мы уже отметили, некоторые традиции утверждают, чтоКосмический человек является целью мироздания, но достижение этой цели невозможно понять как внешнее событие. С точки зрения индусов, например, дело не в том, что внешний мир однажды растворится в первоначальном Великом человеке, а в том, что экстравертная ориентация эго на внешний мир исчезнет, уступив путь Космическому человеку. Это произойдет, когда эго сольется с самостью. Широкий поток проявлений эго (идущий от одной мысли к другой) и его желаний (которые бегут от одного объекта к другому) затихнет в момент, когда внутри будет обретен Великий человек. Действительно, мы никогда не должны забывать, что внешняя реальность для нас существует лишь до тех пор, пока мы ее сознательно воспринимаем, и что мы не можем доказать ее существования «в себе и самой по себе».',
        'Карл Густав Юнг', 'Человек и его символы', null, -977893295, '2023-11-11 07:47:33.566111',
        '2023-11-11 07:47:33.566116', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('336671b2-b439-484b-9ce3-8b427b8b204b',
        'Как показал Юнг, ядро психики (самость) обычно выражает себя в виде четырехуровневой структуры. Число «четыре» также связано с анимой, поскольку ее развитие проходит через четыре стадии. Первую стадию лучше всего символизирует образ Евы: инстинктивные и биологические связи. Вторую стадию - образ Елены в «Фаусте». Она олицетворяет романтический и эстетический уровень, которому еще присущи черты сексуаль-ности. Третья стадия представлена Девой Марией - образом, поднимающим любовь (эрос) до вершин духовной преданно-сти. Четвертую стадию символизирует Сапиенция - высшая мудрость, превосходящая и святость, и чистоту. Другим символом этого уровня является образ Суламифи в Песне Песней Соломона. (Психическое развитие современного человека очень редко достигает данной стадии. Очень близка к такой премудрой аниме Мона Лиза.) На этом этапе мы ограничимся констатацией того, что концепция четверичности (или «кватерности») часто встречается в определенных типах символического материала.',
        'Как показал Юнг, ядро психики (самость) обычно выражает себя в виде четырехуровневой структуры. Число «четыре» также связано с анимой, поскольку ее развитие проходит через четыре стадии. Первую стадию лучше всего символизирует образ Евы: инстинктивные и биологические связи. Вторую стадию - образ Елены в «Фаусте». Она олицетворяет романтический и эстетический уровень, которому еще присущи черты сексуаль-ности. Третья стадия представлена Девой Марией - образом, поднимающим любовь (эрос) до вершин духовной преданно-сти. Четвертую стадию символизирует Сапиенция - высшая мудрость, превосходящая и святость, и чистоту. Другим символом этого уровня является образ Суламифи в Песне Песней Соломона. (Психическое развитие современного человека очень редко достигает данной стадии. Очень близка к такой премудрой аниме Мона Лиза.) На этом этапе мы ограничимся констатацией того, что концепция четверичности (или «кватерности») часто встречается в определенных типах символического материала.',
        'Карл Густав Юнг', 'Человек и его символы', null, 642914185, '2023-11-11 07:47:33.568492',
        '2023-11-11 07:47:33.568494', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('100a5cd2-66fc-44de-858b-3e1023dae5f2',
        'Дыхание шизофреника часто бывает поверхностным. «...результаты недостаточной ассимиляции кислорода проявляются практическиво всех основных симптомах шизофрении. Это ограниченное поле внимания; персеверация; апатия; угнетенность духа; неуместные аффекты, сопровождаемыеглупым смехом; слабая способность рассуждать; непонимание угрожающейопасности; утрата самоконтроля; тревожность; возбуждение без видимыхпричин; неконтролируемые эмоциональные вспышки; внезапная утрата сил при необходимости принять решение и отсутствие воли при принятии ответственности поверхностность (tangentiality) ассоциаций; постепенное помутнение рассудка и ощущение неуместности окружающего мира. Этот список включает в себя многие симптомы, на которые жалуются шизоиды; но пациент не понимает, конечно, что психологические затруднения тесно связаны с физиологическим функционированием. Фактически шизоид не сознает, что удерживает собственное дыхание.',
        'Дыхание шизофреника часто бывает поверхностным. «...результаты недостаточной ассимиляции кислорода проявляются практическиво всех основных симптомах шизофрении. Это ограниченное поле внимания; персеверация; апатия; угнетенность духа; неуместные аффекты, сопровождаемыеглупым смехом; слабая способность рассуждать; непонимание угрожающейопасности; утрата самоконтроля; тревожность; возбуждение без видимыхпричин; неконтролируемые эмоциональные вспышки; внезапная утрата сил при необходимости принять решение и отсутствие воли при принятии ответственности поверхностность (tangentiality) ассоциаций; постепенное помутнение рассудка и ощущение неуместности окружающего мира. Этот список включает в себя многие симптомы, на которые жалуются шизоиды; но пациент не понимает, конечно, что психологические затруднения тесно связаны с физиологическим функционированием. Фактически шизоид не сознает, что удерживает собственное дыхание.',
        'Александр Лоуэн', 'Предательство тела', null, 1557857755, '2023-11-11 07:47:33.570476',
        '2023-11-11 07:47:33.570478', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('86eea9cb-423f-4d13-813e-8dad1c9a250c',
        'Время от времени сталкиваешься с людьми, внешний вид которых действительно похож на монстров. На клиническом семинаре, который проходил в моем оффисе, присутствовал молодой человек, чрезвычайно напоминающий монстра Франкенштейна. Он был такой же негнущийся, с механической походкой, квадратными плечами, глубоко посаженными безжизненными глазами. На его лице застыло выражение монстра из кинофильма ужасов. Сходство просто потрясало, с трудом удавалось диссоциировать этого пациента и кино-монстра. Совершенно удивительным было то, что сам молодой человек совершенно не соответствовал своему внешнему виду. Он был чувствителен, интеллигентен и артистичен. Глубокий анализ его личности выявил, что внешний вид был своеобразным костюмом и маской, которые скрывали и защищали крайнюю чувствительность. Он напомнил мне Хеллоуин и наряд маленького ребенка,который одел его, чтобы остаться неузнанным и испугать кого-то. Фактически, под этой наружностью, пациент был деликатным и испуганным ребенком, который каким-то образом принял такой облик, чтобы защитить себя от нечувствительного мира.',
        'Время от времени сталкиваешься с людьми, внешний вид которых действительно похож на монстров. На клиническом семинаре, который проходил в моем оффисе, присутствовал молодой человек, чрезвычайно напоминающий монстра Франкенштейна. Он был такой же негнущийся, с механической походкой, квадратными плечами, глубоко посаженными безжизненными глазами. На его лице застыло выражение монстра из кинофильма ужасов. Сходство просто потрясало, с трудом удавалось диссоциировать этого пациента и кино-монстра. Совершенно удивительным было то, что сам молодой человек совершенно не соответствовал своему внешнему виду. Он был чувствителен, интеллигентен и артистичен. Глубокий анализ его личности выявил, что внешний вид был своеобразным костюмом и маской, которые скрывали и защищали крайнюю чувствительность. Он напомнил мне Хеллоуин и наряд маленького ребенка,который одел его, чтобы остаться неузнанным и испугать кого-то. Фактически, под этой наружностью, пациент был деликатным и испуганным ребенком, который каким-то образом принял такой облик, чтобы защитить себя от нечувствительного мира.',
        'Александр Лоуэн', 'Предательство тела', null, -1317254890, '2023-11-11 07:47:33.572291',
