INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('cdf03f6e-d4ce-466c-b47c-6c0a7fa31624',
        'Другая форма монстроподобного человеческого существа — «статуя», то есть тело, которое иммобилизовано и сохраняет определенную позу. Качество монстра такому телу придает противоречие между видимым отсутствием жизни и фактом, что эта «статуя» — реальное человеческое существо. Анализ такой личности всегда обнаруживает, что человек, заключенный в статую, — маленький покинутый ребенок. Статуеподобное тело — это его защита против страдания и разочарования, которое он ожидает встретить, если выразит потребность в любви и понимании. Кроме того, это безнадежная попытка получить одобрение, пожертвовав своими чувствами. Статуя сообщает: «Хорошо, я стану таким, как вы хотите, теперь вы будете гордиться мной и любить меня». Но эта иллюзия наталкивается на реальность жизни. Кто сможет полюбить статую? Фрустрация и разочарование, переживаемое в этой позе, усиливает безнадежность и длит иллюзию, что просто необходима более совершенная поза. Кроме того, усиливается внутренняя тревожность, что поза разрушится, и что лежащее за ней отчаяние прорвется и затопит личность.',
        'Другая форма монстроподобного человеческого существа — «статуя», то есть тело, которое иммобилизовано и сохраняет определенную позу. Качество монстра такому телу придает противоречие между видимым отсутствием жизни и фактом, что эта «статуя» — реальное человеческое существо. Анализ такой личности всегда обнаруживает, что человек, заключенный в статую, — маленький покинутый ребенок. Статуеподобное тело — это его защита против страдания и разочарования, которое он ожидает встретить, если выразит потребность в любви и понимании. Кроме того, это безнадежная попытка получить одобрение, пожертвовав своими чувствами. Статуя сообщает: «Хорошо, я стану таким, как вы хотите, теперь вы будете гордиться мной и любить меня». Но эта иллюзия наталкивается на реальность жизни. Кто сможет полюбить статую? Фрустрация и разочарование, переживаемое в этой позе, усиливает безнадежность и длит иллюзию, что просто необходима более совершенная поза. Кроме того, усиливается внутренняя тревожность, что поза разрушится, и что лежащее за ней отчаяние прорвется и затопит личность.',
        'Александр Лоуэн', 'Предательство тела', null, 1384366047, '2023-11-11 07:47:33.574665',
        '2023-11-11 07:47:33.574668', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
