INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('eedb94dc-4ee2-448c-be43-92040cf46d5e',
        'Если питание, сон и дефекация не нормальны, результатом этого становится невозможная запутанность человеческих чувств. Люди попадают в эту путаницу, пытаясь самореализоваться путем творческой деятельности, будто выход лежит в нефизических способах удовлетворения. Во всех случаях, когда творческие усилия заменяют жизнь тела, это ведет только к образу и никогда — к самости (self). Творческая активность удовлетворяет человека и имеет смысл, только когда она обогащает и усиливает жизнь тела, из которого черпает вдохновение.',
        'Если питание, сон и дефекация не нормальны, результатом этого становится невозможная запутанность человеческих чувств. Люди попадают в эту путаницу, пытаясь самореализоваться путем творческой деятельности, будто выход лежит в нефизических способах удовлетворения. Во всех случаях, когда творческие усилия заменяют жизнь тела, это ведет только к образу и никогда — к самости (self). Творческая активность удовлетворяет человека и имеет смысл, только когда она обогащает и усиливает жизнь тела, из которого черпает вдохновение.',
        'Александр Лоуэн', 'Предательство тела', null, 1778188620, '2023-11-11 07:47:33.576776',
        '2023-11-11 07:47:33.576778', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
