INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('c65f76e7-668e-47be-98ef-7386377b6ba2',
        'Существует одно впечатляющее различие между мифом о герое и самим обрядом посвящения. Хрестоматийные героические фигуры истощают свои силы в достижении необходимой цели, и они добиваются успеха даже в том случае, если тотчас же будут наказаны или убиты за свое высокомерие. В противоположность этому новопосвящаемый в процессе обряда инициации призывается оставить волевое устремление и всякое иное желание и полностью подчиниться неизбежности сурового испытания. Он должен быть добровольно готов пережить это испытание без всякой надежды на успех. Фактически он должен быть готов умереть; и хотя символически его суровое испытание может быть сравнительно легким (недлительное воздержание от пищи, выбивание зуба или нанесение татуировки) или мучительным (болезненность операции обрезания, нанесение насечек и разрезов на теле и другие увечья), сама цель всегда остается той же самой: создать символическое настроение смер-ти, из которого может возникнуть символическое настроение возрождения.',
        'Существует одно впечатляющее различие между мифом о герое и самим обрядом посвящения. Хрестоматийные героические фигуры истощают свои силы в достижении необходимой цели, и они добиваются успеха даже в том случае, если тотчас же будут наказаны или убиты за свое высокомерие. В противоположность этому новопосвящаемый в процессе обряда инициации призывается оставить волевое устремление и всякое иное желание и полностью подчиниться неизбежности сурового испытания. Он должен быть добровольно готов пережить это испытание без всякой надежды на успех. Фактически он должен быть готов умереть; и хотя символически его суровое испытание может быть сравнительно легким (недлительное воздержание от пищи, выбивание зуба или нанесение татуировки) или мучительным (болезненность операции обрезания, нанесение насечек и разрезов на теле и другие увечья), сама цель всегда остается той же самой: создать символическое настроение смер-ти, из которого может возникнуть символическое настроение возрождения.',
        'Карл Густав Юнг', 'Человек и его символы', null, -1543525453, '2023-11-11 07:47:35.386349',
        '2023-11-11 07:47:35.386352', 'UNREAD', 50);
