        'Александр Лоуэн', 'Предательство тела', null, -1796601490, '2023-11-11 07:47:35.397631',
        '2023-11-11 07:47:35.397632', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
