        'Карл Густав Юнг', 'Об энергетике души (сборник)', null, -1670264421, '2023-11-11 07:47:38.964578',
        '2023-11-11 07:47:38.964585', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
