INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('31a04e9e-2c03-42da-8273-09d7ba752a71',
        'Шизоид не лишен чувств или страстей, когда встает вопрос защиты его права на преимущества или борьбы за дело. Его приверженность принципам отражает отсутствие Я (selflessness), что является сердцевиной личностных затруднений. Это вовсе не означает, чтозащита справедливости представляет собой исключительно шизоидную черту. Здесь важно то, что шизоид, который не имеет персональной отождествленности, ищет, как правило, справедливости для того, чтобы жить в социальных делах, а также отыскать панацею. Сентиментальность шизоида —результат абстрагирования чувств от тела и от Я (self). Она означает, что шизоид не отождествлен с личностью, и это компенсируется социальными отождествлениями.',
        'Шизоид не лишен чувств или страстей, когда встает вопрос защиты его права на преимущества или борьбы за дело. Его приверженность принципам отражает отсутствие Я (selflessness), что является сердцевиной личностных затруднений. Это вовсе не означает, чтозащита справедливости представляет собой исключительно шизоидную черту. Здесь важно то, что шизоид, который не имеет персональной отождествленности, ищет, как правило, справедливости для того, чтобы жить в социальных делах, а также отыскать панацею. Сентиментальность шизоида —результат абстрагирования чувств от тела и от Я (self). Она означает, что шизоид не отождествлен с личностью, и это компенсируется социальными отождествлениями.',
        'Александр Лоуэн', 'Предательство тела', null, -597132344, '2023-11-11 07:47:39.018851',
        '2023-11-11 07:47:39.018859', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
