VALUES ('c62a760a-2373-42a8-8f0b-02973541c4d0',
        '... на профессиональном языке утрата родителей – это «утрата объекта» (где «объект» является фигурой, играющей важную роль во внутреннем мире человека), в то время как утрата ребенка есть «утрата проекта» (утрата главного организующего жизненного принципа человека, определяющего не только зачем, но и как жить). Неудивительно, что потеря ребенка – это самая тяжелая утрата из всех, и что многие родители скорбят и через пять лет, а некоторые так и не могут оправиться.',
        '... на профессиональном языке утрата родителей – это «утрата объекта» (где «объект» является фигурой, играющей важную роль во внутреннем мире человека), в то время как утрата ребенка есть «утрата проекта» (утрата главного организующего жизненного принципа человека, определяющего не только зачем, но и как жить). Неудивительно, что потеря ребенка – это самая тяжелая утрата из всех, и что многие родители скорбят и через пять лет, а некоторые так и не могут оправиться.',
        'Ирвин Ялом', 'Палач любви и другие психотерапевтические истории', null, 846330092,
        '2023-11-11 07:47:42.818857', '2023-11-11 07:47:42.818867', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('de57bfb8-b0c8-447e-9ab3-bb424e89c5d9',
        'Потерять родителей или старого друга часто означает потерять прошлое: человек, который умер, может быть, был единственным свидетелем золотых дней далекого прошлого. Но потерять ребенка – значит потерять будущее: потерянное есть не что иное, как жизненный проект – то, для чего человек живет, как он проецирует себя в будущее, как он надеется обмануть смерть (ведь ребенок – это залог нашего бессмертия).',
        'Потерять родителей или старого друга часто означает потерять прошлое: человек, который умер, может быть, был единственным свидетелем золотых дней далекого прошлого. Но потерять ребенка – значит потерять будущее: потерянное есть не что иное, как жизненный проект – то, для чего человек живет, как он проецирует себя в будущее, как он надеется обмануть смерть (ведь ребенок – это залог нашего бессмертия).',
        'Ирвин Ялом', 'Палач любви и другие психотерапевтические истории', null, 158150917,
        '2023-11-11 07:47:42.829021', '2023-11-11 07:47:42.829036', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
