INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('817e2d44-52bc-447e-b204-7e2397142dbe',
        'Осмысление ситуации дает нам ощущение господства: чувствуя себя беспомощными и растерянными перед новыми и непонятными явлениями, мы стремимся их объяснить и тем самым ощутить власть над ними. Еще важнее, что смысл порождает ценности и вытекающие из них правила поведения: ответ на вопрос «зачем?» («Зачем я живу?») дает ответ на вопрос «как?» («Как я живу?»).',
        'Осмысление ситуации дает нам ощущение господства: чувствуя себя беспомощными и растерянными перед новыми и непонятными явлениями, мы стремимся их объяснить и тем самым ощутить власть над ними. Еще важнее, что смысл порождает ценности и вытекающие из них правила поведения: ответ на вопрос «зачем?» («Зачем я живу?») дает ответ на вопрос «как?» («Как я живу?»).',
        'Ирвин Ялом', 'Палач любви и другие психотерапевтические истории', null, 1661252319,
        '2023-11-11 07:47:42.850994', '2023-11-11 07:47:42.850998', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('12a74c42-c3fc-4a3b-b53b-708506e8b10a',
        'Джон Гарднер в романе «Грендель» описывает мудреца, который подводит итог своим размышлениям над тайнами жизни двумя простыми, но страшными фразами: «Все угасает. Альтернативы исключают друг друга». О первом утверждении – неизбежности смерти – я уже говорил. Вторая фраза содержит ключ к пониманию трудности любого решения.',
        'Джон Гарднер в романе «Грендель» описывает мудреца, который подводит итог своим размышлениям над тайнами жизни двумя простыми, но страшными фразами: «Все угасает. Альтернативы исключают друг друга». О первом утверждении – неизбежности смерти – я уже говорил. Вторая фраза содержит ключ к пониманию трудности любого решения.',
        'Ирвин Ялом', 'Палач любви и другие психотерапевтические истории', null, 1236140459,
        '2023-11-11 07:47:42.854361', '2023-11-11 07:47:42.854363', 'UNREAD', 50);
