INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('53b68064-53f0-4421-901a-b0662aac2aaf',
        'Описывая шизоидную личность, обычно пользуются фразами, типа «вне тела» или «не весь здесь». Мы ощущаем его отделенность или уход. Это впечатление создают пустые глаза, похожее на маску лицо, ригидное тело и отсутствие спонтанности. Умом он присутствует, как вошедший в поговорку рассеянный профессор, который погружен в свои раздумья. Шизоидный индивидуум сознанием понимает окружающее, но на эмоциональном или телесном уровне с ситуацией не соприкасается. К сожалению, у нас нет отдельного выражения, обозначающего «разумное неприсутствие». «Шизик» — это только слово, обозначающее человека, который присутствует ментально, а эмоционально отсутствует.',
        'Описывая шизоидную личность, обычно пользуются фразами, типа «вне тела» или «не весь здесь». Мы ощущаем его отделенность или уход. Это впечатление создают пустые глаза, похожее на маску лицо, ригидное тело и отсутствие спонтанности. Умом он присутствует, как вошедший в поговорку рассеянный профессор, который погружен в свои раздумья. Шизоидный индивидуум сознанием понимает окружающее, но на эмоциональном или телесном уровне с ситуацией не соприкасается. К сожалению, у нас нет отдельного выражения, обозначающего «разумное неприсутствие». «Шизик» — это только слово, обозначающее человека, который присутствует ментально, а эмоционально отсутствует.',
        'Александр Лоуэн', 'Предательство тела', null, 1932036579, '2023-11-11 07:47:42.881216',
        '2023-11-11 07:47:42.881227', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
