        'Фредерик Перлз', 'Гештальтподход и свидетель терапии', null, 799129457, '2023-11-11 07:47:45.306597',
        '2023-11-11 07:47:45.306597', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
