INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('898d09f6-a0bb-477f-8600-d73aa1af88e0',
        'В нашем обществе проявление эмоций не поощряется; хотя нет сомнений в том, что творческое мышление - любая другая творческая активность - неразрывно связаны с эмоциями, идеалом стало мыслить и жить без эмоций. «Эмоциональность» сделалась синонимом отклонений , неуравновешенности. Приняв этот стандарт, индивид очень сильно себя ослабил; его мышление обеднело, сделалось плоским. С другой стороны, поскольку совсем убить эмоции нельзя, они должны существовать совершенно отдельно от интеллектуальной стороны личности; в результате дешевая неискренняя сентиментальность заполняет фильмы и популярные песни, которые скармливаются миллионам изголодавшихся по эмоциям потребителей.',
        'В нашем обществе проявление эмоций не поощряется; хотя нет сомнений в том, что творческое мышление - любая другая творческая активность - неразрывно связаны с эмоциями, идеалом стало мыслить и жить без эмоций. «Эмоциональность» сделалась синонимом отклонений , неуравновешенности. Приняв этот стандарт, индивид очень сильно себя ослабил; его мышление обеднело, сделалось плоским. С другой стороны, поскольку совсем убить эмоции нельзя, они должны существовать совершенно отдельно от интеллектуальной стороны личности; в результате дешевая неискренняя сентиментальность заполняет фильмы и популярные песни, которые скармливаются миллионам изголодавшихся по эмоциям потребителей.',
        'Эрих Фромм', 'Бегство от свободы', null, 86043984, '2023-11-11 07:47:49.154586', '2023-11-11 07:47:49.154595',
        'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('a31bcce8-bcb3-4fd3-a79c-7fc6d7add7d0',
        'Для Гитлера объектами деструктивности были города и люди. Архитектор, с воодушевлением планировавший переустройство Вены, Линца, Мюнхена и Берлина, он в то же самое время был и тем человеком, который намеревался разрушить Париж, снести с лица Земли Ленинград и в конечном счете уничтожить всю Германию.',
        'Для Гитлера объектами деструктивности были города и люди. Архитектор, с воодушевлением планировавший переустройство Вены, Линца, Мюнхена и Берлина, он в то же самое время был и тем человеком, который намеревался разрушить Париж, снести с лица Земли Ленинград и в конечном счете уничтожить всю Германию.',
        null, 'Мой пациент – Гитлер. Психоанализ фюрера', null, 372035474, '2023-11-11 07:47:49.168242',
        '2023-11-11 07:47:49.168250', 'UNREAD', 50);
