VALUES ('04ee735f-0479-4f8c-9049-1ec82dbf11f7',
        'Нередко демагог, стоящий на грани психоза, спасается от безумия тем, что внешне "сумасшедшие" идеи он выдает за "рациональные". И кажется, что в политической борьбе кое-кто руководствуется не только стремлением к власти, но и необходимостью спастись от безумия.',
        'Нередко демагог, стоящий на грани психоза, спасается от безумия тем, что внешне "сумасшедшие" идеи он выдает за "рациональные". И кажется, что в политической борьбе кое-кто руководствуется не только стремлением к власти, но и необходимостью спастись от безумия.',
        null, 'Мой пациент – Гитлер. Психоанализ фюрера', null, -1927678564, '2023-11-11 07:47:49.188999',
        '2023-11-11 07:47:49.189005', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
