        'Эрих Нойманн', 'Глубинная психология и новая этика', null, -76467033, '2023-11-11 07:47:55.685562',
        '2023-11-11 07:47:55.685565', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
