        'Эрих Нойманн', 'Глубинная психология и новая этика', null, -1972147689, '2023-11-11 07:47:57.536637',
        '2023-11-11 07:47:57.536640', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
