        'Зигмунд Фрейд', 'Толкование сновидений', null, -980716462, '2023-11-11 07:48:12.767701',
        '2023-11-11 07:48:12.767710', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
