        'Алис Миллер', 'Драма одаренного ребенка и поиск собственного Я', null, -1773453374,
        '2023-11-11 07:47:59.450940', '2023-11-11 07:47:59.450947', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
