INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('dbd204f3-54e7-403a-a848-aab61f9233a2',
        'Мы не сможем по-настоящему любить, если нам запрещают знать правду не только о наших родителях и воспитателях, но и о нас самих. Мы можем только симулировать любовь. Но это лицемерное поведение представляет собой полную противоположность любви. Тот, кого мы «любим», оказывается обманутым, у него возникает бессильная ярость, которую приходится загонять внутрь, что в свою очередь приводит к негативным последствиям, особенно если человеку крайне важно верить в любовь. Можно было бы помочь многим людям вести себя более искренне, не вредить самим себе, если бы церковь признала существование элементарных законов человеческой психики. Нужно просто приглядеться к людям и понять, какой страшный вред наносят отношениям в семье и всему обществу в целом лицемерие и ханжество.',
        'Мы не сможем по-настоящему любить, если нам запрещают знать правду не только о наших родителях и воспитателях, но и о нас самих. Мы можем только симулировать любовь. Но это лицемерное поведение представляет собой полную противоположность любви. Тот, кого мы «любим», оказывается обманутым, у него возникает бессильная ярость, которую приходится загонять внутрь, что в свою очередь приводит к негативным последствиям, особенно если человеку крайне важно верить в любовь. Можно было бы помочь многим людям вести себя более искренне, не вредить самим себе, если бы церковь признала существование элементарных законов человеческой психики. Нужно просто приглядеться к людям и понять, какой страшный вред наносят отношениям в семье и всему обществу в целом лицемерие и ханжество.',
        'Алис Миллер', 'Драма одаренного ребенка и поиск собственного Я', null, -176739857,
