INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('39f413f7-d680-4040-bb29-b4f0a4f2f0c1',
        'Еще одним фундаментальным понятием в концепции Бузормени-Надя является парентификация. Парентификация — это инверсия заслуг и долгов, их неверное понимание. Наиболее значимый «долг лояльности семье» — это долг каждого ребенка по отношению к своим родителям за любовь, тепло, заботы, усталость и то особое отношение, которые были получены после его рождения до того момента, когда он стал взрослым. Способ расплатиться за свои долги является трансгенерационным, то есть то, что мы получили от наших родителей, мы отдаем своим детям. Это не мешает платить долги и нашим родителям. Когда они становятся старыми, мы начинаем по-особому относиться к ним, выполняя долг, помогаем им прожить последние годы и перейти от жизни к смерти. Парентификация — это переворачивание ценностей, то есть ситуация, в которой дети, даже малолетние, становятся родителями собственных родителей.',
        'Еще одним фундаментальным понятием в концепции Бузормени-Надя является парентификация. Парентификация — это инверсия заслуг и долгов, их неверное понимание. Наиболее значимый «долг лояльности семье» — это долг каждого ребенка по отношению к своим родителям за любовь, тепло, заботы, усталость и то особое отношение, которые были получены после его рождения до того момента, когда он стал взрослым. Способ расплатиться за свои долги является трансгенерационным, то есть то, что мы получили от наших родителей, мы отдаем своим детям. Это не мешает платить долги и нашим родителям. Когда они становятся старыми, мы начинаем по-особому относиться к ним, выполняя долг, помогаем им прожить последние годы и перейти от жизни к смерти. Парентификация — это переворачивание ценностей, то есть ситуация, в которой дети, даже малолетние, становятся родителями собственных родителей.',
        'Анн Анселин Шутценбергер',
        'Синдром предков. Трансгенерационные связи, семейные тайны, синдром годовщины, передача травм и практическое использование геносоциограммы',
        null, 511157366, '2023-11-11 07:47:59.474365', '2023-11-11 07:47:59.474366', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
