INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('84815a97-0535-472d-8bb6-643f7feb4cc8',
        'Поскольку знание о мире живет в его собственной груди, адепт должен извлекать такого рода знания из знаний о самом себе, ибо самость, которую он стремится познать, есть часть природы, воплощенная благодаря первоначальному единству Бога с миром. Речь явно идет не о познании природы эго, хотя такое знание гораздо удобнее; часто его путают с самопознанием. По этой причине всякого, кто всерьёз пытается познать себя как объект, обвиняют в эгоизме и эксцентричности. Однако такое знание не имеет ничего общего с субъективным знанием эго о самом себе. Эго - собака, которая гоняется за собственным хвостом. Знание, о котором идёт речь, напротив, весьма трудное и требующее моральных усилий исследование, о котором так называемой психологии не известно ничего, а образованной публике - крайне мало.',
