INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('1c94960e-20d7-4e83-8752-473115cd363e',
        'Младенческая амнезия имеет место потому, что мы, по большей части, выражаем свою эксплицитную память словами, которые младенцам, только-только научившимся ходить, ещё предстоит выучить, а также потому, что главный участок мозга, отвечающий за хранение эксплицитных воспоминаний (гиппокамп), - это одна из тех структур головного мозга, которые созревают в последнюю очередь. Мы не помним очень многого из собственного прошлого. Тем не менее некоторую часть того, что мы не можем вспомнить в эксплицитной, сознательной форме, мы помним имплицитно, интуитивно.',
        'Младенческая амнезия имеет место потому, что мы, по большей части, выражаем свою эксплицитную память словами, которые младенцам, только-только научившимся ходить, ещё предстоит выучить, а также потому, что главный участок мозга, отвечающий за хранение эксплицитных воспоминаний (гиппокамп), - это одна из тех структур головного мозга, которые созревают в последнюю очередь. Мы не помним очень многого из собственного прошлого. Тем не менее некоторую часть того, что мы не можем вспомнить в эксплицитной, сознательной форме, мы помним имплицитно, интуитивно.',
        'Дэвид Майерс', 'Интуиция', null, -868679777, '2023-11-11 07:48:05.114687', '2023-11-11 07:48:05.114688',
        'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('33c9bfce-982d-4c99-bfb9-429438ccf3ff',
        'Быть красивой (или иметь успех) — вопрос совсем не анатомии, а родительского разрешения. Анатомия делает девушку хорошенькой или фотогеничной, но только улыбка отца позволяет лицу дочери сверкать красотой. Дети совершают поступки для кого-то. Мальчик старается добиться успеха в спорте или учебе для матери, а девочка быть умной, красивой, а впоследствии и плодовитой — для отца.',
        'Быть красивой (или иметь успех) — вопрос совсем не анатомии, а родительского разрешения. Анатомия делает девушку хорошенькой или фотогеничной, но только улыбка отца позволяет лицу дочери сверкать красотой. Дети совершают поступки для кого-то. Мальчик старается добиться успеха в спорте или учебе для матери, а девочка быть умной, красивой, а впоследствии и плодовитой — для отца.',
        'Эрик Берн', 'Игры, в которые играют люди: Психология человеческих взаимоотношений', null, 1023482837,
        '2023-11-11 07:48:05.116061', '2023-11-11 07:48:05.116062', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('d6e3ce6c-731a-488e-8c49-b98427cebc5d',
        'Что я хотел от нее? С ее точки зрения, она была очень откровенна. Я должен был точно сформулировать, что меня не устраивало. Что в ее признаниях оставляло меня равнодушным? Мне пришло в голову, что она все время признавалась в чем-то, случившемся в другое время и в другом месте. Бетти была неспособна или не хотела раскрыться в настоящий момент, в котором присутствовали мы оба. Отсюда ее уклончивые ответы «хорошо» и «нормально», которые появлялись каждый раз, когда я спрашивал о ее чувствах здесь и сейчас. Это было первым важным открытием, которое я сделал в отношении Бетти: она была совершенно отделена от других и могла вынести эту изоляцию лишь благодаря мифу о том, что ее подлинная личная жизнь протекает где-то еще. В первый раз я начал подозревать, что для Бетти не существует «здесь». Еще одно соображение: если со мной она была более откровенна, чем с другими, то какими же были ее близкие отношения? Бетти ответила, что у нее репутация хорошего собеседника. У нас с ней, сказала она, один и тот же бизнес: она была всеобщим психотерапевтом. Она добавила, что у нее много друзей, но никто из них не знает ее. Ее фирменным знаком было то, что она умеет слушать и что она забавная. Эта мысль была ей ненавистна, но она точно соответствовала стереотипу жизнерадостной толстухи. Это непосредственно вело к пониманию другой причины, по которой Бетти казалась мне такой скучной: она играла передо мной свою роль – в наших разговорах она никогда не была самой собой, она все время притворялась и показывала фальшивое веселье.',
        'Что я хотел от нее? С ее точки зрения, она была очень откровенна. Я должен был точно сформулировать, что меня не устраивало. Что в ее признаниях оставляло меня равнодушным? Мне пришло в голову, что она все время признавалась в чем-то, случившемся в другое время и в другом месте. Бетти была неспособна или не хотела раскрыться в настоящий момент, в котором присутствовали мы оба. Отсюда ее уклончивые ответы «хорошо» и «нормально», которые появлялись каждый раз, когда я спрашивал о ее чувствах здесь и сейчас. Это было первым важным открытием, которое я сделал в отношении Бетти: она была совершенно отделена от других и могла вынести эту изоляцию лишь благодаря мифу о том, что ее подлинная личная жизнь протекает где-то еще. В первый раз я начал подозревать, что для Бетти не существует «здесь». Еще одно соображение: если со мной она была более откровенна, чем с другими, то какими же были ее близкие отношения? Бетти ответила, что у нее репутация хорошего собеседника. У нас с ней, сказала она, один и тот же бизнес: она была всеобщим психотерапевтом. Она добавила, что у нее много друзей, но никто из них не знает ее. Ее фирменным знаком было то, что она умеет слушать и что она забавная. Эта мысль была ей ненавистна, но она точно соответствовала стереотипу жизнерадостной толстухи. Это непосредственно вело к пониманию другой причины, по которой Бетти казалась мне такой скучной: она играла передо мной свою роль – в наших разговорах она никогда не была самой собой, она все время притворялась и показывала фальшивое веселье.',
        'Ирвин Ялом', 'Палач любви', null, -489373483, '2023-11-11 07:48:06.850478', '2023-11-11 07:48:06.850481',
        'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('e17b8a9a-eb11-4a7e-b3c7-4ef15876a980',
        'Мне всегда казалось, что то, как человек встречает смерть, в огромной степени зависит от модели, заложенной родителями. Последний дар, который родитель может дать своим детям, – это на собственном примере научить их, как встретить собственную смерть, сохраняя самообладание. И Карлос дал своим детям необычайный урок благости. Его смерть не стала одним из темных, скрытых, потаенных уходов. До самого конца он и его дети были откровенны друг с другом относительно его болезни и вместе шутили над его манерой пыхтеть, косить глазами и морщить губы, когда он произносил слово «лимфо-о-о-ома». А мне он преподнес свой главный дар незадолго до смерти, и это был окончательный ответ на вопрос, стоит ли и уместно ли заниматься «амбициозной» терапией со смертельно больными людьми. Когда я навестил его в госпитале, Карлос был так слаб, что почти не мог двигаться, но он поднял голову, пожал мне руку и прошептал: «Спасибо. Спасибо, что спасли мою жизнь!»',
        'Мне всегда казалось, что то, как человек встречает смерть, в огромной степени зависит от модели, заложенной родителями. Последний дар, который родитель может дать своим детям, – это на собственном примере научить их, как встретить собственную смерть, сохраняя самообладание. И Карлос дал своим детям необычайный урок благости. Его смерть не стала одним из темных, скрытых, потаенных уходов. До самого конца он и его дети были откровенны друг с другом относительно его болезни и вместе шутили над его манерой пыхтеть, косить глазами и морщить губы, когда он произносил слово «лимфо-о-о-ома». А мне он преподнес свой главный дар незадолго до смерти, и это был окончательный ответ на вопрос, стоит ли и уместно ли заниматься «амбициозной» терапией со смертельно больными людьми. Когда я навестил его в госпитале, Карлос был так слаб, что почти не мог двигаться, но он поднял голову, пожал мне руку и прошептал: «Спасибо. Спасибо, что спасли мою жизнь!»',
        'Ирвин Ялом', 'Палач любви', null, 1039116378, '2023-11-11 07:48:06.896971', '2023-11-11 07:48:06.896976',
        'UNREAD', 50);
