        'Эрих Фромм', 'Адольф Гитлер. Клинический случай некрофилии', null, 791324263, '2023-11-11 07:48:08.939076',
        '2023-11-11 07:48:08.939082', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
