INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('01416b4b-3ecf-42ec-b691-cd96a0a81626',
        'Уолтер и Пеллер предложили пять основных  предположений, расширяющих эти три основных правила:  1) концентрация на успехах приводит к конструктивным изменениям;  2) понимание, что у каждой проблемы есть исключения, когда она не возникает, помогает клиентам найти ее решение;  3) маленькие положительные  изменения ведут к большим позитивным переменам;  4) все клиенты  могут решать свои проблемы, выявляя, детализируя и повторяя успешные действия, характерные для исключений; и  5) цели должны формулироваться позитивно, допускать измерение и предполагать активность.',
        'Уолтер и Пеллер [Walter &amp; Peller, 1992] предложили пять основных  предположений, расширяющих эти три основных правила:  1) концентрация на успехах приводит к конструктивным изменениям;  2) понимание, что у каждой проблемы есть исключения, когда она не возникает, помогает клиентам найти ее решение;  3) маленькие положительные  изменения ведут к большим позитивным переменам;  4) все клиенты  могут решать свои проблемы, выявляя, детализируя и повторяя успешные действия, характерные для исключений; и  5) цели должны формулироваться позитивно, допускать измерение и предполагать активность.',
        'Эрфорд Брэдли Т.', '45 основных техник для психологического консультирования', null, -1136533568,
        '2023-11-11 07:47:14.624287', '2023-11-11 07:47:14.624290', 'UNREAD', 50);
